sys_script_client.script:
function onLoad() {
	var strDataExp = g_form.getValue('u_date_expiration');
	var partesData = strDataExp.split("-");
	var dataExp = new Date(partesData[2], partesData[1] - 1, partesData[0]);
	alert("Antes do IF");
	var elementID = document.getElementById('x_serv4_portal_cer_certification.u_date_expiration');
	if(dataExp > new Date()){
		g_form.addInfoMessage("Ainda Não Expirou");
		elementID.style.backgroundColor = "green";
        elementID.style.color = "black";

		//elementID.style.backgroundColor = "green";
		//corverde('x_serv4_portal_cer_certification.u_date_expiration');
		//elementID.style.color = "red";
	}else{
		g_form.addInfoMessage("Já Expirou");
		elementID.style.backgroundColor = "red";
		elementID.style.color = "black";
		//elementID.style.backgroundColor = "red";
		//elementID.style.color = "black";
	}alert("Depois do IF");
}
